tables.table_schema:
CREATE TABLE maps (
    map_id INT PRIMARY KEY,
    map_name VARCHAR(50),
    map_data JSON
);

CREATE TABLE MapData (
    map_id INT PRIMARY KEY,
    map_name VARCHAR(255),
    map_data JSON
);

CREATE TABLE Coordinates (
    coord_id INT PRIMARY KEY,
    longitude FLOAT,
    latitude FLOAT
);

CREATE TABLE Boundaries (
    boundary_id INT PRIMARY KEY,
    boundary_name VARCHAR(255),
    boundary_data JSON
);
