tables.table_schema:
CREATE TABLE roads (
    road_id INT PRIMARY KEY,
    road_name VARCHAR(50) NOT NULL,
    road_condition VARCHAR(50) CHECK (road_condition IN ('Good', 'Fair', 'Poor'))
);

CREATE TABLE utilities (
    utility_id INT PRIMARY KEY,
    utility_type VARCHAR(50) NOT NULL,
    utility_location VARCHAR(50) NOT NULL
);

CREATE TABLE Roads (
    road_id INT PRIMARY KEY,
    road_name VARCHAR(255) NOT NULL,
    road_length FLOAT NOT NULL
);

CREATE TABLE Bridges (
    bridge_id INT PRIMARY KEY,
    bridge_name VARCHAR(255) NOT NULL,
    bridge_type VARCHAR(50) NOT NULL,
    bridge_capacity INT NOT NULL
);

CREATE TABLE Utilities (
    utility_id INT PRIMARY KEY,
    utility_name VARCHAR(255) NOT NULL,
    utility_type VARCHAR(50) NOT NULL
);
