tables.table_schema:
CREATE TABLE public_transportation_usage (
    date DATE,
    route_id INT,
    total_passengers INT
);

CREATE TABLE service_efficiency (
    route_id INT,
    on_time_performance FLOAT
);

CREATE TABLE public_transportation_data (
    trip_id INT PRIMARY KEY,
    date DATE,
    route_id INT,
    passenger_count INT
);

CREATE TABLE transportation_routes (
    route_id INT PRIMARY KEY,
    route_name VARCHAR(50),
    start_location VARCHAR(100),
    end_location VARCHAR(100)
);

CREATE TABLE station_locations (
    station_id SERIAL PRIMARY KEY,
    station_name VARCHAR(255),
    city VARCHAR(100),
    country VARCHAR(100)
);
