tables.table_schema:
CREATE TABLE persons_of_interest (
    id INT PRIMARY KEY,
    name VARCHAR(50),
    age INT,
    face_encoding BYTEA
);

CREATE TABLE known_faces (
    face_id INT PRIMARY KEY,
    face_encoding BYTEA
);

CREATE TABLE persons_of_interest (
    person_id INT PRIMARY KEY,
    name VARCHAR(50),
    face_image BYTEA,
    description TEXT
);

CREATE TABLE known_faces (
    face_id INT PRIMARY KEY,
    face_image BYTEA,
    person_id INT,
    FOREIGN KEY (person_id) REFERENCES persons_of_interest(person_id)
);

CREATE TABLE queries (
    query_id INT PRIMARY KEY,
    query_image BYTEA,
    result_person_id INT,
    FOREIGN KEY (result_person_id) REFERENCES persons_of_interest(person_id)
);

CREATE TABLE Person(id INT PRIMARY KEY, name VARCHAR(100), age INT, occupation VARCHAR(100));
CREATE TABLE KnownFaces(id INT PRIMARY KEY, name VARCHAR(100), image_url TEXT);
CREATE TABLE FaceRecognitionResults(id INT PRIMARY KEY, face_id INT, person_id INT, confidence FLOAT);
CREATE TABLE FaceFeatures(face_id INT PRIMARY KEY, feature_vector ARRAY<DOUBLE>);
