tables.table_schema:
CREATE TABLE employee_authentication (
  id INT PRIMARY KEY,
  employee_id INT NOT NULL,
  fingerprint_scan VARCHAR(100) NOT NULL,
  date_scanned TIMESTAMP NOT NULL
);

CREATE TABLE employee_authentications (
    employee_id INT PRIMARY KEY,
    fingerprint_scan BYTEA,
    access_level INT
);

CREATE TABLE employees (
    id INT PRIMARY KEY,
    name VARCHAR(50),
    fingerprint_scan BLOB
);

CREATE TABLE departments (
    dept_id INT PRIMARY KEY,
    dept_name VARCHAR(50)
);

CREATE TABLE employee_logins (
    login_id INT PRIMARY KEY,
    employee_id INT,
    login_time TIMESTAMP
);
