tables.table_schema:
CREATE TABLE FingerprintImages (
 id SERIAL PRIMARY KEY,
 image_name VARCHAR(100),
 image_data BYTEA,
 metadata JSON
);

CREATE TABLE fingerprint_metadata (
  fingerprint_id UUID PRIMARY KEY,
  image_location VARCHAR(255) NOT NULL,
  capture_date TIMESTAMP NOT NULL,
  resolution VARCHAR(20) NOT NULL
);

CREATE TABLE fingerprint_images (
    image_id INT PRIMARY KEY,
    image_name VARCHAR(255),
    image_data BYTEA,
    capture_date TIMESTAMP
);

CREATE TABLE FingerprintImages (
 id INT PRIMARY KEY,
 image_data BYTEA,
 captured_date TIMESTAMP,
 analyzed BOOLEAN
);

CREATE TABLE FingerprintFeatures (
 id INT PRIMARY KEY,
 fingerprint_id INT,
 feature_data JSON,
 extracted_date TIMESTAMP
);

CREATE TABLE ImageMetadata (
 id INT PRIMARY KEY,
 image_id INT,
 metadata_key VARCHAR(50),
 metadata_value VARCHAR(100)
);
