tables.table_schema:
CREATE TABLE voice_data_patterns (
    id SERIAL PRIMARY KEY,
    voice_sample_id UUID NOT NULL,
    audio_file_path VARCHAR NOT NULL,
    transcription TEXT
);

CREATE TABLE voice_data_patterns (
  id INT PRIMARY KEY,
  audio_file_path VARCHAR(255),
  timestamp TIMESTAMP,
  voice_pattern JSON
);

CREATE TABLE speech_recognition_accuracy (
  id INT PRIMARY KEY,
  audio_id INT,
  recognized_text VARCHAR(255),
  confidence_level FLOAT
);

CREATE TABLE voice_data_patterns (
    user_id INT,
    audio_data BLOB,
    timestamp TIMESTAMP
);

CREATE TABLE voice_data (
    user_id UUID,
    audio_file BYTEA,
    transcription TEXT,
    analysis_result JSONB
);

CREATE TABLE user_profiles (
    user_id UUID,
    first_name TEXT,
    last_name TEXT,
    email TEXT
);

CREATE TABLE speech_recognition (
    audio_file BYTEA,
    recognized_text TEXT,
    confidence FLOAT
);

CREATE TABLE data_analysis (
    analysis_id UUID,
    analysis_type TEXT,
    analysis_result JSONB
);
