tables.table_schema:
CREATE TABLE user_activity_logs (
    user_id INT PRIMARY KEY,
    timestamp TIMESTAMP,
    activity_type VARCHAR,
    activity_description TEXT
);

CREATE TABLE UserActivityLogs (
    user_id INT PRIMARY KEY,
    activity_type VARCHAR(255),
    activity_timestamp TIMESTAMP,
    activity_details JSON
);

CREATE TABLE VoiceRecognitionSessions (
    session_id SERIAL PRIMARY KEY,
    user_id INT REFERENCES UserActivityLogs(user_id),
    session_start TIMESTAMP,
    session_end TIMESTAMP
);

CREATE TABLE UserActivityLogs (
    log_id INT PRIMARY KEY,
    user_id INT NOT NULL,
    activity_type VARCHAR NOT NULL,
    activity_timestamp TIMESTAMP NOT NULL
);

CREATE TABLE VoiceRecognitionTasks (
    task_id INT PRIMARY KEY,
    user_id INT NOT NULL,
    audio_file_name VARCHAR NOT NULL,
    task_status VARCHAR DEFAULT 'Pending'
);

CREATE TABLE RecognizedPhrases (
    phrase_id INT PRIMARY KEY,
    task_id INT NOT NULL,
    recognized_phrase TEXT NOT NULL,
    confidence_level DECIMAL NOT NULL
);

CREATE TABLE VoiceRecognitionPerformance (
    performance_id INT PRIMARY KEY,
    user_id INT NOT NULL,
    recognition_accuracy DECIMAL NOT NULL,
    recognition_speed DECIMAL NOT NULL
);

CREATE TABLE user_activity_logs (
    user_id INT,
    activity_time TIMESTAMP,
    activity_type VARCHAR,
    activity_description TEXT
);

CREATE TABLE voice_recognition_data (
    data_id SERIAL,
    audio_sample_id INT,
    user_id INT,
    recognition_result TEXT
);

CREATE TABLE audio_samples (
    audio_sample_id INT,
    sample_duration TIME,
    sample_format VARCHAR,
    sample_data BYTEA
);

CREATE TABLE user_devices (
    user_id INT,
    device_id SERIAL,
    device_name VARCHAR,
    device_type VARCHAR
);

CREATE TABLE device_logs (
    log_id SERIAL,
    device_id INT,
    log_time TIMESTAMP,
    log_details TEXT
);
