tables.table_schema:
CREATE TABLE voice_sentiment_data (
    interaction_id UUID PRIMARY KEY,
    participant_id UUID,
    sentiment_score DECIMAL,
    speech_text TEXT
);

CREATE TABLE emotional_recognition (
    interaction_id UUID PRIMARY KEY,
    participant_id UUID,
    emotion_category VARCHAR(50),
    confidence_level DECIMAL
);

CREATE TABLE VoiceSentimentData (
    id INT PRIMARY KEY,
    recording_id INT,
    sentiment_score FLOAT,
    text_transcription STRING
);

CREATE TABLE SpeechInteractions (
    interaction_id INT PRIMARY KEY,
    user_id INT,
    timestamp TIMESTAMP,
    interaction_type STRING
);

CREATE TABLE EmotionRecognition (
    emotion_id INT PRIMARY KEY,
    recording_id INT,
    emotion_category STRING,
    confidence_level FLOAT
);

CREATE TABLE voice_sentiments (
  id INT PRIMARY KEY,
  user_id INT,
  sentiment_score FLOAT,
  recorded_timestamp TIMESTAMP
);

CREATE TABLE users (
  user_id INT PRIMARY KEY,
  username VARCHAR(50),
  age INT,
  gender VARCHAR(10)
);

CREATE TABLE interactions (
  interaction_id INT PRIMARY KEY,
  user_id INT,
  other_user_id INT,
  interaction_timestamp TIMESTAMP
);

CREATE TABLE emotions (
  emotion_id INT PRIMARY KEY,
  emotion_name VARCHAR(20),
  description TEXT
);

CREATE TABLE voice_sentiment_data (
    id INT PRIMARY KEY,
    speech_text TEXT,
    sentiment_score INT,
    recording_date TIMESTAMP
);

CREATE TABLE speaker_info (
    speaker_id INT PRIMARY KEY,
    speaker_name TEXT,
    speaker_gender TEXT,
    speaker_age INT
);

CREATE TABLE emotion_labels (
    emotion_id INT PRIMARY KEY,
    emotion_name TEXT
);

CREATE TABLE sentiment_feedback (
    feedback_id INT PRIMARY KEY,
    interaction_id INT,
    feedback_text TEXT,
    feedback_date TIMESTAMP
);
