tables.table_schema:
CREATE TABLE palm_vein_data (
    id INT PRIMARY KEY,
    palm_id INT NOT NULL,
    vein_data BYTEA NOT NULL
);

CREATE TABLE users (
    user_id INT PRIMARY KEY,
    username VARCHAR(50) NOT NULL,
    palm_id INT NOT NULL,
    FOREIGN KEY (palm_id) REFERENCES palm_vein_data(palm_id)
);

CREATE TABLE palm_vein_users (
    id SERIAL PRIMARY KEY,
    name TEXT NOT NULL,
    palm_vein_data BYTEA NOT NULL
);

CREATE TABLE palm_vein_authentication (
    id SERIAL PRIMARY KEY,
    user_id INT NOT NULL,
    authentication_result BOOLEAN NOT NULL,
    timestamp TIMESTAMP NOT NULL
);

CREATE TABLE palm_vein_logs (
    id SERIAL PRIMARY KEY,
    user_id INT NOT NULL,
    log_message TEXT NOT NULL,
    log_timestamp TIMESTAMP NOT NULL
);

CREATE TABLE palm_vein_data (
    user_id INT PRIMARY KEY,
    palm_vein_image BYTEA,
    palm_vein_template VARCHAR(255)
);

CREATE TABLE user_credentials (
    user_id INT PRIMARY KEY,
    username VARCHAR(50),
    password VARCHAR(50)
);

CREATE TABLE login_attempts (
    attempt_id SERIAL PRIMARY KEY,
    user_id INT,
    login_time TIMESTAMP,
    login_result BOOLEAN
);

CREATE TABLE access_logs (
    log_id SERIAL PRIMARY KEY,
    user_id INT,
    access_time TIMESTAMP
);
