tables.table_schema:
CREATE TABLE palm_vein_templates (
    user_id INT PRIMARY KEY,
    palm_template BYTEA
);

CREATE TABLE palm_vein_templates (
    id SERIAL PRIMARY KEY,
    user_id INT NOT NULL,
    palm_template BYTEA NOT NULL,
    created_at TIMESTAMP NOT NULL
);

CREATE TABLE access_logs (
    log_id SERIAL PRIMARY KEY,
    user_id INT NOT NULL,
    access_time TIMESTAMP NOT NULL,
    access_granted BOOLEAN NOT NULL
);

CREATE TABLE PalmVeinTemplates (
    id SERIAL PRIMARY KEY,
    palm_vein_template BYTEA NOT NULL
);

CREATE TABLE Employees (
    employee_id SERIAL PRIMARY KEY,
    employee_name VARCHAR(50) NOT NULL,
    palm_vein_id INTEGER REFERENCES PalmVeinTemplates(id) NOT NULL
);

CREATE TABLE AccessLogs (
    log_id SERIAL PRIMARY KEY,
    employee_id INTEGER REFERENCES Employees(employee_id) NOT NULL,
    access_time TIMESTAMPTZ NOT NULL
);

CREATE TABLE AccessControlSystem (
    system_id SERIAL PRIMARY KEY,
    system_name VARCHAR(50) NOT NULL
);

CREATE TABLE EmployeeAccessRights (
    access_id SERIAL PRIMARY KEY,
    employee_id INTEGER REFERENCES Employees(employee_id) NOT NULL,
    system_id INTEGER REFERENCES AccessControlSystem(system_id) NOT NULL
);
