tables.table_schema:
CREATE TABLE PalmVeinRecognitionData (
    id INT PRIMARY KEY,
    user_id INT,
    vein_pattern TEXT,
    scan_date TIMESTAMP
);

CREATE TABLE palm_vein_recognition_data (
  user_id INT,
  palm_vein_id VARCHAR(255),
  verification_result VARCHAR(50),
  timestamp TIMESTAMP
);

CREATE TABLE access_logs (
  log_id INT,
  user_id INT,
  access_type VARCHAR(20),
  access_time TIMESTAMP
);

CREATE TABLE palm_vein_data (
  id UUID PRIMARY KEY,
  user_id UUID NOT NULL,
  vein_pattern VARCHAR(255) NOT NULL,
  date_time TIMESTAMP DEFAULT now()
);

CREATE TABLE access_logs (
  log_id UUID PRIMARY KEY,
  user_id UUID NOT NULL,
  access_time TIMESTAMP DEFAULT now(),
  action VARCHAR(50) NOT NULL
);

CREATE TABLE PalmVeinData (
    id INT PRIMARY KEY,
    subject_id INT,
    palm_image BYTEA,
    palm_feature_vector BYTEA
);

CREATE TABLE HistoricalMatchResults (
    match_id INT PRIMARY KEY,
    palm_data1_id INT,
    palm_data2_id INT,
    match_score FLOAT
);

CREATE TABLE TrendAnalysis (
    trend_id INT PRIMARY KEY,
    trend_name TEXT,
    trend_description TEXT
);

CREATE TABLE UserPermissions (
    user_id INT PRIMARY KEY,
    can_access_palm_data BOOLEAN,
    can_run_analysis BOOLEAN
);

CREATE TABLE AuditLogs (
    log_id INT PRIMARY KEY,
    action_description TEXT,
    timestamp TIMESTAMP
);
