tables.table_schema:
CREATE TABLE palm_vein_templates (
    user_id INT PRIMARY KEY,
    palm_vein_template BYTEA,
    encryption_key VARCHAR(50)
);

CREATE TABLE PalmVeinBiometricTemplates (
    id UUID PRIMARY KEY,
    user_id UUID NOT NULL,
    template_data BYTEA NOT NULL,
    encryption_key BYTEA NOT NULL
);

CREATE TABLE UserAccessLogs (
    log_id UUID PRIMARY KEY,
    user_id UUID NOT NULL,
    timestamp TIMESTAMP NOT NULL,
    action VARCHAR(50) NOT NULL
);

CREATE TABLE palm_vein_access_logs ( 
   log_id INT PRIMARY KEY, 
   user_id INT NOT NULL, 
   accessed_at TIMESTAMP NOT NULL DEFAULT NOW(), 
   access_type VARCHAR(20) NOT NULL 
 );

CREATE TABLE palm_vein_security_settings ( 
   setting_id INT PRIMARY KEY, 
   encryption_key VARCHAR(255) NOT NULL, 
   key_expiration_date DATE NOT NULL 
 );
