tables.table_schema:
CREATE TABLE iris_scans (
  scan_id UUID PRIMARY KEY,
  individual_id UUID REFERENCES individuals(id),
  scan_date TIMESTAMP,
  scan_result VARCHAR
);

CREATE TABLE iris_scans (
   person_id INT PRIMARY KEY,
   iris_scan_data BYTES
);

CREATE TABLE individuals (
   person_id INT PRIMARY KEY,
   first_name VARCHAR,
   last_name VARCHAR,
   age INT
);

CREATE TABLE iris_match (
   match_id INT PRIMARY KEY,
   person_id_a INT,
   person_id_b INT,
   match_similarity FLOAT
);

CREATE TABLE individuals (
    individual_id INT PRIMARY KEY,
    iris_scan_data BYTEA
);
