tables.table_schema:
CREATE TABLE faces (
    id INT PRIMARY KEY,
    name VARCHAR(50),
    image_data BYTEA
);

CREATE TABLE face_logs (
    log_id SERIAL PRIMARY KEY,
    face_id INT,
    timestamp TIMESTAMP,
    event VARCHAR(50)
);

CREATE TABLE facial_recognition_data (
    id INT PRIMARY KEY,
    image_url TEXT,
    face_embedding BYTEA,
    timestamp TIMESTAMP
);

CREATE TABLE identified_faces (
    id INT PRIMARY KEY,
    face_id INT,
    person_name TEXT,
    confidence FLOAT
);

CREATE TABLE real_time_identification (
    id INT PRIMARY KEY,
    face_id INT,
    timestamp TIMESTAMP,
    result TEXT
);

CREATE TABLE face_images (
    image_id INT PRIMARY KEY,
    image_data BYTEA,
    image_description TEXT,
    timestamp TIMESTAMP
);

CREATE TABLE face_features (
    feature_id INT PRIMARY KEY,
    image_id INT REFERENCES face_images(image_id),
    feature_vector BYTEA
);

CREATE TABLE face_recognition (
    record_id INT PRIMARY KEY,
    feature_id INT REFERENCES face_features(feature_id),
    recognition_result TEXT,
    confidence FLOAT
);

CREATE TABLE face_history (
    history_id INT PRIMARY KEY,
    record_id INT REFERENCES face_recognition(record_id),
    user_id INT,
    timestamp TIMESTAMP
);

CREATE TABLE facial_recognition_data (
    id SERIAL PRIMARY KEY,
    person_name VARCHAR(100),
    image_data BYTEA,
    face_encoding FLOAT[],
    capture_timestamp TIMESTAMP
);

CREATE TABLE face_recognition_logs (
    log_id SERIAL PRIMARY KEY,
    person_id INT REFERENCES facial_recognition_data(id),
    log_timestamp TIMESTAMP,
    log_message TEXT
);

CREATE TABLE recognized_faces (
    face_id SERIAL PRIMARY KEY,
    person_id INT REFERENCES facial_recognition_data(id),
    confidence_score FLOAT,
    recognition_timestamp TIMESTAMP
);

CREATE TABLE known_faces (
    face_id SERIAL PRIMARY KEY,
    person_name VARCHAR(100) UNIQUE,
    known_face_encoding FLOAT[],
    added_timestamp TIMESTAMP
);

CREATE TABLE face_recognition_settings (
    setting_id SERIAL PRIMARY KEY,
    recognition_threshold FLOAT,
    detection_model VARCHAR(50),
    recognition_algorithm VARCHAR(50)
);
