tables.table_schema:
CREATE TABLE user_data (
  user_id INT PRIMARY KEY,
  username VARCHAR(50) NOT NULL,
  behavioral_biometrics JSONB
);

CREATE TABLE user_preferences (
   user_id INT PRIMARY KEY,
   theme VARCHAR(255),
   language VARCHAR(255),
   font_size INT
);

CREATE TABLE user_preferences (
    user_id INT PRIMARY KEY,
    background_color VARCHAR,
    font_size INT,
    theme VARCHAR
);

CREATE TABLE behavior_data (
    user_id INT,
    behavior_type VARCHAR,
    timestamp TIMESTAMP,
    FOREIGN KEY (user_id) REFERENCES user_preferences(user_id)
);

CREATE TABLE behavioral_biometrics (
    user_id INT,
    biometric_type VARCHAR,
    biometric_data VARCHAR,
    FOREIGN KEY (user_id) REFERENCES user_preferences(user_id)
);

CREATE TABLE user_preferences_table (
    preference_id SERIAL PRIMARY KEY,
    user_id INT REFERENCES user_profile_table(user_id),
    theme VARCHAR(20) DEFAULT 'light',
    language VARCHAR(20) DEFAULT 'english'
);

CREATE TABLE user_behavior_table (
    behavior_id SERIAL PRIMARY KEY,
    user_id INT REFERENCES user_profile_table(user_id),
    page_visited VARCHAR(100) NOT NULL,
    timestamp TIMESTAMP NOT NULL
);

CREATE TABLE biometrics_data_table (
    biometric_id SERIAL PRIMARY KEY,
    user_id INT REFERENCES user_profile_table(user_id),
    heart_rate INT,
    temperature FLOAT
);

CREATE TABLE personalized_experience_table (
    experience_id SERIAL PRIMARY KEY,
    user_id INT REFERENCES user_profile_table(user_id),
    experience_description TEXT,
    timestamp TIMESTAMP
);
