tables.table_schema:
CREATE TABLE encrypted_user_data (
    user_id INT NOT NULL,
    behavior_data TEXT NOT NULL,
    encryption_key TEXT NOT NULL
);

CREATE TABLE behavior_patterns (
    pattern_id INT NOT NULL,
    user_id INT NOT NULL,
    pattern_data TEXT NOT NULL,
    pattern_date TIMESTAMP NOT NULL
);

CREATE TABLE user_behavior_data (
    id INT PRIMARY KEY,
    encrypted_data TEXT,
    timestamp TIMESTAMP
);

CREATE TABLE user_profiles (
    user_id INT PRIMARY KEY,
    username VARCHAR(50),
    encrypted_behavior_data TEXT
);

CREATE TABLE UserBehaviorData (
  user_id INT,
  behavior_data BYTEA,
  encrypted_data BYTEA
);

CREATE TABLE UserCredentials (
  user_id INT,
  username VARCHAR,
  password VARCHAR
);

CREATE TABLE SessionData (
  session_id INT,
  user_id INT,
  login_time TIMESTAMP,
  logout_time TIMESTAMP
);

CREATE TABLE BiometricsData (
  user_id INT,
  biometric_info BYTEA
);

CREATE TABLE login_history (
    login_id UUID PRIMARY KEY,
    user_id UUID NOT NULL,
    login_time TIMESTAMP NOT NULL,
    login_status BOOLEAN NOT NULL
);

CREATE TABLE user_behavior (
    behavior_id UUID PRIMARY KEY,
    user_id UUID NOT NULL,
    behavior_type VARCHAR(50) NOT NULL,
    timestamp TIMESTAMP NOT NULL
);

CREATE TABLE behavioral_analysis (
    analysis_id UUID PRIMARY KEY,
    behavior_id UUID NOT NULL,
    analysis_result TEXT NOT NULL,
    analysis_time TIMESTAMP NOT NULL
);
