tables.table_schema:
CREATE TABLE user_behavior (
    user_id INT PRIMARY KEY,
    username VARCHAR(50),
    session_id INT,
    behavior_pattern VARCHAR(200)
);

CREATE TABLE anomaly_detection (
    detection_id INT PRIMARY KEY,
    user_id INT,
    anomaly_type VARCHAR(50),
    detection_timestamp TIMESTAMP
);

CREATE TABLE behavior_anomalies (
    anomaly_id SERIAL PRIMARY KEY,
    user_id INT,
    anomaly_details JSON,
    detection_time TIMESTAMP
);

CREATE TABLE user_profile (
    user_id SERIAL PRIMARY KEY,
    email VARCHAR(255) NOT NULL,
    first_name VARCHAR(50) NOT NULL,
    last_name VARCHAR(50) NOT NULL
);

CREATE TABLE login_history (
    login_id SERIAL PRIMARY KEY,
    user_id INT NOT NULL,
    login_time TIMESTAMP NOT NULL
);

CREATE TABLE session_data (
    session_id SERIAL PRIMARY KEY,
    user_id INT NOT NULL,
    start_time TIMESTAMP NOT NULL,
    end_time TIMESTAMP
);

CREATE TABLE behavior_data (
    behavior_id SERIAL PRIMARY KEY,
    user_id INT NOT NULL,
    behavior_type VARCHAR(50) NOT NULL,
    behavior_timestamp TIMESTAMP NOT NULL
);

CREATE TABLE anomaly_detection (
    anomaly_id SERIAL PRIMARY KEY,
    behavior_id INT NOT NULL,
    anomaly_type VARCHAR(50) NOT NULL,
    anomaly_description TEXT
);
