tables.table_schema:
CREATE TABLE RetinaScanningData (
    user_id UUID PRIMARY KEY,
    scan_time TIMESTAMP NOT NULL,
    retina_image BYTEA NOT NULL
);

CREATE TABLE RetinaBiometrics (
    id UUID PRIMARY KEY,
    user_id UUID NOT NULL,
    retina_scan_data BYTEA NOT NULL,
    scan_date TIMESTAMP NOT NULL
);

CREATE TABLE Users (
    user_id UUID PRIMARY KEY,
    full_name VARCHAR(100) NOT NULL,
    email VARCHAR(100) NOT NULL,
    registration_date TIMESTAMP NOT NULL
);

CREATE TABLE retina_scans (
    user_id INT PRIMARY KEY,
    scan_time TIMESTAMP,
    scan_data BYTEA
);

CREATE TABLE auth_logs (
    log_id SERIAL PRIMARY KEY,
    user_id INT,
    action VARCHAR(50),
    timestamp TIMESTAMP
);
