tables.table_schema:
CREATE TABLE RetinaScanningData (
    patient_id INT PRIMARY KEY,
    scan_id INT UNIQUE,
    scan_date DATE,
    scan_data BYTEA
);

CREATE TABLE EyeHealthTrends (
    trend_id SERIAL PRIMARY KEY,
    patient_id INT,
    trend_date DATE,
    trend_description TEXT
);

CREATE TABLE DiseaseProgression (
    progression_id SERIAL PRIMARY KEY,
    patient_id INT,
    progression_date DATE,
    progression_description TEXT
);
