tables.table_schema:
CREATE TABLE RetinaScanningData (
    id INT PRIMARY KEY,
    eye_scan_id VARCHAR(50) NOT NULL,
    image_data BYTEA NOT NULL,
    matching_score FLOAT NOT NULL
);

CREATE TABLE CriminalCases (
    case_id SERIAL PRIMARY KEY,
    suspect_id INT NOT NULL,
    crime_type VARCHAR(50) NOT NULL,
    investigation_status BOOLEAN NOT NULL
);

CREATE TABLE RetinaScanData (
    scan_id INT PRIMARY KEY,
    eye_color VARCHAR(255),
    retina_image BYTEA
);

CREATE TABLE CriminalRecords (
    criminal_id INT PRIMARY KEY,
    criminal_name VARCHAR(255),
    criminal_photo BYTEA
);

CREATE TABLE MatchedResults (
    match_id INT PRIMARY KEY,
    scan_id INT REFERENCES RetinaScanData(scan_id),
    criminal_id INT REFERENCES CriminalRecords(criminal_id)
);

CREATE TABLE RetinaScans (
    scan_id UUID PRIMARY KEY,
    eye_scan_data BLOB,
    person_id UUID
);

CREATE TABLE Persons (
    person_id UUID PRIMARY KEY,
    full_name VARCHAR(100),
    date_of_birth DATE,
    gender VARCHAR(10)
);

CREATE TABLE CriminalCases (
    case_id UUID PRIMARY KEY,
    case_description TEXT,
    suspect_id UUID
);

CREATE TABLE Suspects (
    suspect_id UUID PRIMARY KEY,
    person_id UUID,
    case_id UUID
);

CREATE TABLE eye_scans (
	eye_scan_id INT PRIMARY KEY,
	person_id INT,
	eye_image BLOB
);

CREATE TABLE criminals (
	criminal_id INT PRIMARY KEY,
	criminal_name VARCHAR(50),
	crime_details TEXT
);

CREATE TABLE investigations (
	investigation_id INT PRIMARY KEY,
	investigator_id INT,
	criminal_id INT
);

CREATE TABLE investigators (
	investigator_id INT PRIMARY KEY,
	investigator_name VARCHAR(50),
	department VARCHAR(50)
);

CREATE TABLE matches (
	match_id INT PRIMARY KEY,
	eye_scan_id INT,
	criminal_id INT
);
