tables.table_schema:
CREATE TABLE retina_scanning_data (
    user_id INT PRIMARY KEY,
    eye_image BYTEA,
    scan_date TIMESTAMP,
    detected_disease TEXT
);

CREATE TABLE RetinaScanningData (
	patient_id INT,
	patient_name VARCHAR(50),
	scan_date DATE,
	eye_image BYTEA
);

CREATE TABLE EyeDiseases (
	disease_id INT,
	disease_name VARCHAR(50),
	description TEXT
);

CREATE TABLE RetinaUsers (
    user_id UUID PRIMARY KEY,
    username VARCHAR(50) NOT NULL,
    email VARCHAR(100) NOT NULL,
    age INT
);

CREATE TABLE RetinaImages (
    image_id UUID PRIMARY KEY,
    user_id UUID REFERENCES RetinaUsers(user_id),
    image_url VARCHAR(255) NOT NULL,
    timestamp TIMESTAMP NOT NULL
);

CREATE TABLE RetinaAnalysis (
    analysis_id UUID PRIMARY KEY,
    image_id UUID REFERENCES RetinaImages(image_id),
    diagnosis VARCHAR(100) NOT NULL,
    confidence DECIMAL(5,2) NOT NULL
);

CREATE TABLE RetinaScanningData (
    patient_id INT PRIMARY KEY,
    scan_date DATE,
    scan_result TEXT
);

CREATE TABLE EyeDiseases (
    disease_id INT PRIMARY KEY,
    disease_name TEXT,
    description TEXT
);

CREATE TABLE EyeDisorders (
    disorder_id INT PRIMARY KEY,
    patient_id INT,
    disorder_name TEXT,
    diagnosis_date DATE
);

CREATE TABLE Clinic (
    clinic_id INT PRIMARY KEY,
    clinic_name TEXT,
    location TEXT
);

CREATE TABLE RetinaImages (
    image_id INT NOT NULL,
    patient_id INT NOT NULL,
    image_data BYTEA NOT NULL,
    scan_date DATE NOT NULL
);

CREATE TABLE EyeConditions (
    condition_id INT NOT NULL,
    patient_id INT NOT NULL,
    condition_name VARCHAR(50) NOT NULL,
    diagnosis_date DATE NOT NULL
);

CREATE TABLE ImageAnalysis (
    analysis_id INT NOT NULL,
    image_id INT NOT NULL,
    analysis_result JSONB NOT NULL,
    analysis_date DATE NOT NULL
);

CREATE TABLE Patients (
    patient_id INT NOT NULL,
    first_name VARCHAR(50) NOT NULL,
    last_name VARCHAR(50) NOT NULL,
    age INT NOT NULL,
    gender CHAR(1) NOT NULL
);

CREATE TABLE Clinicians (
    clinician_id INT NOT NULL,
    first_name VARCHAR(50) NOT NULL,
    last_name VARCHAR(50) NOT NULL,
    specialization VARCHAR(50) NOT NULL
);
