tables.table_schema:
CREATE TABLE employee_retina_data (
    employee_id INT PRIMARY KEY,
    retina_scan_data BYTEA
);

CREATE TABLE access_log (
    log_id SERIAL PRIMARY KEY,
    employee_id INT,
    access_time TIMESTAMP,
    access_result VARCHAR
);

CREATE TABLE Department (
    department_id SERIAL PRIMARY KEY,
    department_name VARCHAR(50) NOT NULL,
    department_head VARCHAR(100)
);

CREATE TABLE AccessControl (
    access_id SERIAL PRIMARY KEY,
    employee_id INT REFERENCES EmployeeInformation(employee_id),
    access_location VARCHAR(100) NOT NULL,
    access_date TIMESTAMP
);

CREATE TABLE RetinaScanningLogs (
    log_id SERIAL PRIMARY KEY,
    employee_id INT REFERENCES EmployeeInformation(employee_id),
    scan_timestamp TIMESTAMP,
    scan_result VARCHAR(20)
);

CREATE TABLE SecurityClearance (
    clearance_id SERIAL PRIMARY KEY,
    employee_id INT REFERENCES EmployeeInformation(employee_id),
    clearance_level VARCHAR(20) NOT NULL,
    clearance_expiry_date DATE
);
