tables.table_schema:
CREATE TABLE RetinaScanningData (
    user_id INT PRIMARY KEY,
    retina_data BYTEA,
    transaction_date TIMESTAMP
);

CREATE TABLE RetinaScanningData (
    user_id INT PRIMARY KEY,
    retina_scan_results TEXT
);

CREATE TABLE Retina_Scanning_Data (
    retina_id INT PRIMARY KEY,
    user_id INT,
    scan_time TIMESTAMP,
    scan_data BLOB
);

CREATE TABLE Biometric_Authentication (
    bio_auth_id INT PRIMARY KEY,
    user_id INT,
    auth_method VARCHAR(50),
    auth_status BOOLEAN
);

CREATE TABLE Financial_Transactions (
    transaction_id INT PRIMARY KEY,
    user_id INT,
    transaction_amount DECIMAL(10,2),
    transaction_date DATE
);

CREATE TABLE User_Information (
    user_id INT PRIMARY KEY,
    user_name VARCHAR(50),
    user_email VARCHAR(50),
    user_phone VARCHAR(15)
);

CREATE TABLE retina_scans (
    id INT PRIMARY KEY,
    user_id INT NOT NULL,
    scan_data STRING NOT NULL,
    scan_date TIMESTAMP
);

CREATE TABLE users (
    user_id INT PRIMARY KEY,
    full_name STRING NOT NULL,
    email STRING UNIQUE,
    password STRING NOT NULL
);

CREATE TABLE transactions (
    transaction_id INT PRIMARY KEY,
    user_id INT NOT NULL,
    amount DECIMAL NOT NULL,
    transaction_date TIMESTAMP
);

CREATE TABLE merchants (
    merchant_id INT PRIMARY KEY,
    merchant_name STRING NOT NULL,
    location STRING NOT NULL
);

CREATE TABLE authentication_logs (
    log_id INT PRIMARY KEY,
    user_id INT NOT NULL,
    scan_id INT NOT NULL,
    log_date TIMESTAMP
);
