tables.table_schema:
CREATE TABLE RetinaScanningData (
    id SERIAL PRIMARY KEY,
    user_id UUID NOT NULL,
    image_data BYTEA NOT NULL,
    scan_timestamp TIMESTAMP NOT NULL
);

CREATE TABLE RetinaScanningData (
    user_id INT,
    retina_image BLOB,
    scan_date DATE,
    PRIMARY KEY (user_id)
);

CREATE TABLE Retina_Scanning_Device (
  device_id INT PRIMARY KEY,
  user_id INT,
  retina_data BYTEA,
  scan_date TIMESTAMP
);

CREATE TABLE User_Data (
  user_id INT PRIMARY KEY,
  username VARCHAR(50),
  email VARCHAR(255),
  phone_number VARCHAR(15)
);

CREATE TABLE Device_Logs (
  log_id INT PRIMARY KEY,
  device_id INT,
  log_message TEXT,
  log_timestamp TIMESTAMP
);

CREATE TABLE User_Logs (
  log_id INT PRIMARY KEY,
  user_id INT,
  log_message TEXT,
  log_timestamp TIMESTAMP
);
