tables.table_schema:
CREATE TABLE facial_recognition_data (
    id SERIAL PRIMARY KEY,
    image_url TEXT,
    facial_features JSONB,
    detection_timestamp TIMESTAMP
);

CREATE TABLE facial_analysis_results (
    result_id SERIAL PRIMARY KEY,
    image_id INT REFERENCES facial_recognition_data(id),
    analysis_date DATE,
    analysis_result JSONB
);

CREATE TABLE FaceImages (
    face_id INT PRIMARY KEY,
    image_data BYTEA,
    timestamp TIMESTAMP
);

CREATE TABLE FaceFeatures (
    face_id INT PRIMARY KEY,
    features JSON,
    created_at TIMESTAMP 
);

CREATE TABLE FaceEvents (
    event_id INT PRIMARY KEY,
    face_id INT,
    event_type VARCHAR(50),
    event_timestamp TIMESTAMP
);
