CREATE TABLE employees (
    employee_id INT PRIMARY KEY,
    first_name VARCHAR(50),
    last_name VARCHAR(50),
    hand_geometry_data BYTEA
);

CREATE TABLE EmployeeHandGeometry (
    employee_id INT,
    hand_geometry_data BLOB,
    access_level VARCHAR(20)
);

CREATE TABLE EmployeeDetails (
    employee_id INT,
    first_name VARCHAR(50),
    last_name VARCHAR(50),
    department VARCHAR(50)
);

CREATE TABLE EmployeeAccessLog (
    log_id INT,
    employee_id INT,
    access_time TIMESTAMP,
    access_location VARCHAR(100)
);

CREATE TABLE employees (
    employee_id INT PRIMARY KEY,
    full_name VARCHAR(100) NOT NULL,
    hand_geometry_data BYTEA NOT NULL
);

CREATE TABLE departments (
    department_id INT PRIMARY KEY,
    department_name VARCHAR(100) NOT NULL
);

CREATE TABLE access_levels (
    level_id INT PRIMARY KEY,
    level_name VARCHAR(100) NOT NULL
);

CREATE TABLE employee_access (
    employee_id INT REFERENCES employees(employee_id),
    level_id INT REFERENCES access_levels(level_id),
    PRIMARY KEY (employee_id, level_id)
);

CREATE TABLE employees (
    employee_id INT PRIMARY KEY,
    hand_geometry_data BLOB
);

CREATE TABLE departments (
    department_id INT PRIMARY KEY,
    department_name VARCHAR(50)
);

CREATE TABLE access_levels (
    level_id INT PRIMARY KEY,
    level_name VARCHAR(50)
);

CREATE TABLE employee_access (
    employee_id INT,
    level_id INT,
    FOREIGN KEY (employee_id) REFERENCES employees(employee_id),
    FOREIGN KEY (level_id) REFERENCES access_levels(level_id)
);

CREATE TABLE department_employees (
    department_id INT,
    employee_id INT,
    FOREIGN KEY (department_id) REFERENCES departments(department_id),
    FOREIGN KEY (employee_id) REFERENCES employees(employee_id)
);
