tables.table_schema:
CREATE TABLE hand_geometry_data (
    employee_id INT,
    hand_geometry_image BYTEA,
    hand_measurement_x DECIMAL,
    hand_measurement_y DECIMAL
);

CREATE TABLE hand_geometry_data (
  employee_id INT PRIMARY KEY,
  hand_measurement_1 FLOAT,
  hand_measurement_2 FLOAT,
  hand_measurement_3 FLOAT
);

CREATE TABLE employee_movements (
  movement_id INT PRIMARY KEY,
  employee_id INT,
  movement_type VARCHAR,
  movement_timestamp TIMESTAMP
);

CREATE TABLE employee_hand_data (
  employee_id INT NOT NULL,
  hand_geometry_data JSONB NOT NULL,
  timestamp TIMESTAMP NOT NULL
);

CREATE TABLE facility_access (
  access_id INT NOT NULL,
  employee_id INT NOT NULL,
  access_type VARCHAR(50) NOT NULL,
  access_timestamp TIMESTAMP NOT NULL
);

CREATE TABLE employee (
  employee_id SERIAL PRIMARY KEY,
  employee_name VARCHAR(255) NOT NULL,
  employee_department VARCHAR(100),
  hired_date DATE
);

CREATE TABLE facility (
  facility_id SERIAL PRIMARY KEY,
  facility_name VARCHAR(255) NOT NULL,
  location VARCHAR(100),
  capacity INT
);

CREATE TABLE employee_movement (
  movement_id SERIAL PRIMARY KEY,
  employee_id INT NOT NULL,
  facility_id INT NOT NULL,
  timestamp TIMESTAMP
);

CREATE TABLE employees (
    employee_id INT PRIMARY KEY,
    first_name VARCHAR(50),
    last_name VARCHAR(50),
    hand_geometry_data BLOB
);

CREATE TABLE employee_access_logs (
    log_id INT PRIMARY KEY,
    employee_id INT,
    timestamp TIMESTAMP,
    action VARCHAR(50)
);

CREATE TABLE access_areas (
    area_id INT PRIMARY KEY,
    area_name VARCHAR(50)
);

CREATE TABLE access_logs (
    log_id INT PRIMARY KEY,
    area_id INT,
    timestamp TIMESTAMP,
    status VARCHAR(50)
);
