tables.table_schema:
CREATE TABLE hand_geometry_data (
  hand_id INT PRIMARY KEY,
  hand_image BYTEA,
  hand_length FLOAT,
  hand_width FLOAT
);

CREATE TABLE hand_geometry_data (
    hand_id SERIAL PRIMARY KEY,
    hand_length REAL NOT NULL,
    hand_width REAL NOT NULL,
    finger_count INTEGER NOT NULL
);

CREATE TABLE authentication_log (
    log_id SERIAL PRIMARY KEY,
    hand_id INTEGER REFERENCES hand_geometry_data(hand_id),
    authentication_time TIMESTAMP NOT NULL
);

CREATE TABLE hand_geometry_data (
    hand_id UUID PRIMARY KEY,
    hand_image BYTEA NOT NULL,
    hand_measurement JSONB
);

CREATE TABLE user_profiles (
    user_id UUID PRIMARY KEY,
    full_name VARCHAR(100) NOT NULL,
    username VARCHAR(50) UNIQUE,
    email VARCHAR(100) UNIQUE
);

CREATE TABLE access_logs (
    log_id SERIAL PRIMARY KEY,
    user_id UUID NOT NULL,
    access_time TIMESTAMP DEFAULT CURRENT_TIMESTAMP
);

CREATE TABLE HandGeometryData (
 hand_id INT PRIMARY KEY,
 palm_width FLOAT,
 finger_length FLOAT,
 hand_shape VARCHAR(50)
 );

CREATE TABLE UserAuth (
 user_id INT PRIMARY KEY,
 username VARCHAR(50),
 password VARCHAR(50)
 );

CREATE TABLE AccessLogs (
 log_id INT PRIMARY KEY,
 user_id INT,
 access_time TIMESTAMP,
 access_location VARCHAR(100)
 );

CREATE TABLE SecuritySettings (
 setting_id INT PRIMARY KEY,
 setting_name VARCHAR(50),
 setting_value VARCHAR(50)
 );

CREATE TABLE HandGeometryData (
	user_id INT PRIMARY KEY,
	hand_geometry BLOB
);

CREATE TABLE UserAccess (
	user_id INT PRIMARY KEY,
	access_level INT
);

CREATE TABLE HandGeometryLog (
	log_id INT PRIMARY KEY,
	user_id INT,
	access_time TIMESTAMP
);

CREATE TABLE SecurityClearance (
	clearance_id INT PRIMARY KEY,
	clearance_level INT
);

CREATE TABLE HandGeometryAudit (
	audit_id INT PRIMARY KEY,
	log_id INT,
	audit_time TIMESTAMP
);
