tables.table_schema:
CREATE TABLE facial_recognition_results (
    id INT PRIMARY KEY,
    person_id INT,
    recognition_score FLOAT,
    image_path TEXT
);

CREATE TABLE facial_recognition_logs (
    log_id INT PRIMARY KEY,
    recognition_time TIMESTAMP,
    result_id INT,
    person_id INT
);

CREATE TABLE FacialRecognitionResults (
    id INT PRIMARY KEY,
    subject_name VARCHAR(100),
    face_image bytea,
    recognition_timestamp TIMESTAMP
);

CREATE TABLE FaceMatches (
    match_id INT PRIMARY KEY,
    face_id1 INT,
    face_id2 INT,
    match_score FLOAT
);

CREATE TABLE IdentifiedSubjects (
    subject_id INT PRIMARY KEY,
    subject_name VARCHAR(100),
    total_recognitions INT
);

CREATE TABLE RecognitionLogs (
    log_id INT PRIMARY KEY,
    recognition_timestamp TIMESTAMP,
    log_message TEXT
);
