tables.table_schema:
CREATE TABLE signature_patterns (
    signature_id INT PRIMARY KEY,
    signature_image BYTEA,
    signature_date DATE
);

CREATE TABLE signature_identifications (
    identification_id INT PRIMARY KEY,
    signature_id INT,
    identification_date DATE,
    identified_person_name VARCHAR(50)
);

CREATE TABLE SignaturePatterns (
  signature_id INT PRIMARY KEY,
  signature_text TEXT,
  signature_date TIMESTAMP
);

CREATE TABLE Users (
  user_id INT PRIMARY KEY,
  user_name TEXT,
  user_password TEXT
);

CREATE TABLE UserSignatures (
  user_id INT,
  signature_id INT,
  FOREIGN KEY (user_id) REFERENCES Users(user_id),
  FOREIGN KEY (signature_id) REFERENCES SignaturePatterns(signature_id)
);

CREATE TABLE signature_data (
    id SERIAL PRIMARY KEY,
    signature_id INT NOT NULL,
    pattern TEXT NOT NULL,
    timestamp TIMESTAMP NOT NULL
);

CREATE TABLE user (
    user_id SERIAL PRIMARY KEY,
    full_name VARCHAR(50) NOT NULL,
    email VARCHAR(50) UNIQUE,
    password VARCHAR(50) NOT NULL
);

CREATE TABLE signature_identifier (
    identifier_id SERIAL PRIMARY KEY,
    user_id INT NOT NULL,
    signature_id INT NOT NULL,
    confidence_level FLOAT NOT NULL
);

CREATE TABLE signature_image (
    image_id SERIAL PRIMARY KEY,
    user_id INT NOT NULL,
    image_data BYTEA NOT NULL,
    image_format VARCHAR(10) NOT NULL
);

CREATE TABLE signature_patterns (
    signature_id INT PRIMARY KEY,
    signature_data VARCHAR(255),
    signature_timestamp TIMESTAMP
);

CREATE TABLE users (
    user_id INT PRIMARY KEY,
    user_name VARCHAR(50),
    user_email VARCHAR(100),
    user_phone VARCHAR(15)
);

CREATE TABLE departments (
    department_id INT PRIMARY KEY,
    department_name VARCHAR(50)
);
