tables.table_schema:
CREATE TABLE signature_verification_requests (
  request_id INT PRIMARY KEY,
  timestamp TIMESTAMPTZ,
  location GEOGRAPHY
);

CREATE TABLE signature_verification_requests (
  request_id SERIAL PRIMARY KEY,
  timestamp TIMESTAMPTZ NOT NULL,
  location GEOGRAPHY
);

CREATE TABLE verification_logs (
  log_id SERIAL PRIMARY KEY,
  request_id INT REFERENCES signature_verification_requests(request_id),
  verification_status VARCHAR(50),
  log_timestamp TIMESTAMPTZ
);

CREATE TABLE SignatureVerificationRequests (
    request_id UUID PRIMARY KEY,
    timestamp TIMESTAMP,
    geolocation POINT
);
CREATE TABLE Users (
    user_id UUID PRIMARY KEY,
    username VARCHAR(50),
    email VARCHAR(100),
    location POINT
);
CREATE TABLE VerificationHistory (
    verification_id UUID PRIMARY KEY,
    request_id UUID,
    user_id UUID,
    verification_timestamp TIMESTAMP
);

CREATE TABLE SignatureVerificationRequest (
  request_id SERIAL PRIMARY KEY,
  timestamp TIMESTAMP,
  latitude FLOAT,
  longitude FLOAT
);

CREATE TABLE LocationData (
  location_id SERIAL PRIMARY KEY,
  location_name VARCHAR(50),
  city VARCHAR(50),
  country VARCHAR(50)
);

CREATE TABLE SignatureVerificationHistory (
  history_id SERIAL PRIMARY KEY,
  request_id INT REFERENCES SignatureVerificationRequest(request_id),
  verification_status VARCHAR(20),
  verification_timestamp TIMESTAMP
);
