tables.table_schema:
CREATE TABLE signature_mismatches (
    id SERIAL PRIMARY KEY,
    user_id INT NOT NULL,
    frequency INT NOT NULL,
    timestamp TIMESTAMP NOT NULL
);

CREATE TABLE signature_mismatches (
    id INT PRIMARY KEY,
    user_id INT,
    signature VARCHAR(255),
    mismatch_count INT
 );

CREATE TABLE flagged_activities (
    id INT PRIMARY KEY,
    user_id INT,
    activity_type VARCHAR(50),
    reason TEXT
 );

CREATE TABLE fraudulent_users (
    id INT PRIMARY KEY,
    user_id INT,
    flag_reason TEXT
 );
