tables.table_schema:
CREATE TABLE Signature_Template (
    id UUID PRIMARY KEY,
    individual_id UUID UNIQUE,
    signature_image BYTEA
);

CREATE TABLE SignatureTemplates (
    signature_id UUID PRIMARY KEY,
    individual_id UUID,
    template_data JSONB
);

CREATE TABLE Individuals (
    individual_id UUID PRIMARY KEY,
    full_name VARCHAR(100),
    birth_date DATE,
    signature_id UUID,
    UNIQUE (full_name)
);

CREATE TABLE SignatureTemplate (
    id INT NOT NULL,
    name VARCHAR(50) NOT NULL,
    signature_image BYTEA NOT NULL,
    PRIMARY KEY (id)
);

CREATE TABLE Individual (
    individual_id INT NOT NULL,
    full_name VARCHAR(100) NOT NULL,
    date_of_birth DATE NOT NULL,
    PRIMARY KEY (individual_id)
);

CREATE TABLE signature_templates (
    id UUID PRIMARY KEY,
    template_name VARCHAR(255) NOT NULL,
    individual_id UUID NOT NULL,
    template_data JSONB
);

CREATE TABLE individuals (
    id UUID PRIMARY KEY,
    name VARCHAR(255) NOT NULL,
    age INT,
    gender VARCHAR(10)
);

CREATE TABLE recognition_accuracy (
    id UUID PRIMARY KEY,
    individual_id UUID NOT NULL,
    accuracy_score FLOAT
);

CREATE TABLE recognition_logs (
    id UUID PRIMARY KEY,
    individual_id UUID NOT NULL,
    log_timestamp TIMESTAMP
);

CREATE TABLE signatures (
  id INT PRIMARY KEY,
  signature_data VARCHAR(255),
  user_id INT
);

CREATE TABLE users (
  user_id INT PRIMARY KEY,
  first_name VARCHAR(50),
  last_name VARCHAR(50),
  unique_identifier UUID
);

CREATE TABLE recognition_accuracy (
  id INT PRIMARY KEY,
  user_id INT,
  accuracy_score FLOAT
);

CREATE TABLE recognition_history (
  id INT PRIMARY KEY,
  user_id INT,
  recognition_time TIMESTAMP
);

CREATE TABLE user_identifiers (
  id INT PRIMARY KEY,
  user_id INT,
  biometric_data BLOB,
  biography TEXT
);
