tables.table_schema:
CREATE TABLE signature_data (
    signature_id INT PRIMARY KEY,
    signature_name VARCHAR(255),
    signature_date TIMESTAMP,
    signature_description TEXT
);

CREATE TABLE signatures (
    signature_id INT PRIMARY KEY,
    signature_text VARCHAR(255),
    signature_date DATE
);

CREATE TABLE signatures (
   id UUID PRIMARY KEY,
   signature_data TEXT,
   signature_date TIMESTAMP
);

CREATE TABLE investigations (
   investigation_id UUID PRIMARY KEY,
   investigator_name TEXT,
   case_id UUID,
   investigation_date TIMESTAMP
);

CREATE TABLE reports (
   report_id UUID PRIMARY KEY,
   investigation_id UUID,
   report_data TEXT,
   report_date TIMESTAMP
);

CREATE TABLE signature_data (
    signature_id INT PRIMARY KEY,
    signature VARCHAR(255),
    data_source VARCHAR(100),
    timestamp TIMESTAMP
);

CREATE TABLE signatures (
    signature_id SERIAL PRIMARY KEY,
    signature_text TEXT NOT NULL,
    signature_date TIMESTAMP NOT NULL
);

CREATE TABLE signers (
    signer_id SERIAL PRIMARY KEY,
    signer_name TEXT NOT NULL,
    signing_date DATE NOT NULL
);

CREATE TABLE locations (
    location_id SERIAL PRIMARY KEY,
    location_name TEXT NOT NULL,
    location_latitude FLOAT,
    location_longitude FLOAT
);

CREATE TABLE investigation_logs (
    log_id SERIAL PRIMARY KEY,
    report_id INT NOT NULL,
    investigator_name TEXT NOT NULL,
    investigation_notes TEXT
);
