    id INT PRIMARY KEY,
    signature VARCHAR(255) NOT NULL,
    user_id INT,
    access_level VARCHAR(50) NOT NULL
);

CREATE TABLE signature_data (
  id INT PRIMARY KEY,
  signature TEXT
);

CREATE TABLE signature_data (id INT PRIMARY KEY,
user_id INT,
signature TEXT);

CREATE TABLE access_control (user_id INT PRIMARY KEY,
role ENUM('Admin','User'),
permissions TEXT);

CREATE TABLE user_credentials (user_id INT PRIMARY KEY,
username VARCHAR(50),
password VARCHAR(50));

CREATE TABLE signatures (
    signature_id UUID PRIMARY KEY,
    user_id UUID NOT NULL,
    signature_text TEXT NOT NULL
);

CREATE TABLE users (
    user_id UUID PRIMARY KEY,
    username VARCHAR(50) NOT NULL,
    password VARCHAR(50) NOT NULL
);

CREATE TABLE permissions (
    permission_id UUID PRIMARY KEY,
    user_id UUID NOT NULL,
    permission_type VARCHAR(50) NOT NULL
);

CREATE TABLE roles (
    role_id UUID PRIMARY KEY,
    role_name VARCHAR(50) NOT NULL,
    description TEXT
);

CREATE TABLE roles (role_id SERIAL PRIMARY KEY,
  role_name VARCHAR(50) UNIQUE NOT NULL,
  description TEXT);

CREATE TABLE role_permissions (role_id INT REFERENCES roles (role_id),
  permission_id INT REFERENCES permissions (permission_id),
  PRIMARY KEY (role_id, permission_id));
