tables.table_schema:
CREATE TABLE facial_recognition_logs (
    log_id SERIAL PRIMARY KEY,
    user_id INT REFERENCES facial_recognition_users(user_id),
    timestamp TIMESTAMPTZ NOT NULL,
    action VARCHAR(50) NOT NULL
);

CREATE TABLE FaceRecognitionUsers (
    user_id UUID PRIMARY KEY,
    username VARCHAR(50) NOT NULL,
    password VARCHAR(100) NOT NULL
);

CREATE TABLE FaceRecognitionLogs (
    log_id SERIAL PRIMARY KEY,
    user_id UUID REFERENCES FaceRecognitionUsers(user_id),
    timestamp TIMESTAMP NOT NULL,
    action VARCHAR(50) NOT NULL
);

CREATE TABLE FaceRecognitionData (
    data_id UUID PRIMARY KEY,
    user_id UUID REFERENCES FaceRecognitionUsers(user_id),
    image_url VARCHAR(255) NOT NULL,
    data_type VARCHAR(50) NOT NULL
);

CREATE TABLE users (
  user_id UUID PRIMARY KEY,
  username VARCHAR(50) NOT NULL,
  email VARCHAR(100) NOT NULL,
  face_encoding BYTEA
);

CREATE TABLE user_faces (
  face_id UUID PRIMARY KEY,
  user_id UUID REFERENCES users(user_id),
  face_data BYTEA NOT NULL,
  created_at TIMESTAMP DEFAULT now()
);

CREATE TABLE face_recognition_logs (
  log_id UUID PRIMARY KEY,
  user_id UUID REFERENCES users(user_id),
  recognized_at TIMESTAMP DEFAULT now()
);

CREATE TABLE face_recognition_events (
  event_id UUID PRIMARY KEY,
  event_type VARCHAR(50) NOT NULL,
  event_data JSONB,
  event_timestamp TIMESTAMP DEFAULT now()
);

CREATE TABLE User (
    user_id UUID PRIMARY KEY,
    username VARCHAR(50) NOT NULL,
    password VARCHAR(50) NOT NULL,
    email VARCHAR(100) UNIQUE NOT NULL
);

CREATE TABLE Image (
    image_id UUID PRIMARY KEY,
    user_id UUID REFERENCES User(user_id),
    image_data BYTEA NOT NULL,
    timestamp TIMESTAMP DEFAULT NOW()
);

CREATE TABLE RecognitionData (
    recognition_id UUID PRIMARY KEY,
    image_id UUID REFERENCES Image(image_id),
    recognition_result JSONB NOT NULL,
    timestamp TIMESTAMP DEFAULT NOW()
);

CREATE TABLE Log (
    log_id UUID PRIMARY KEY,
    user_id UUID REFERENCES User(user_id),
    action_description TEXT NOT NULL,
    timestamp TIMESTAMP DEFAULT NOW()
);

CREATE TABLE Report (
    report_id UUID PRIMARY KEY,
    user_id UUID REFERENCES User(user_id),
    report_data TEXT NOT NULL,
    creation_date DATE DEFAULT CURRENT_DATE
);
