    sequence_id UUID PRIMARY KEY,
    species TEXT NOT NULL,
    length INT NOT NULL
);

CREATE TABLE sequence_analysis (
    analysis_id UUID PRIMARY KEY,
    sequence_id UUID,
    analysis_date DATE,
    analysis_result TEXT
);

