tables.table_schema:
CREATE TABLE gene_sequences (
  gene_id INT PRIMARY KEY,
  gene_name VARCHAR(100),
  sequence TEXT
);

CREATE TABLE genes (
    gene_id UUID PRIMARY KEY,
    gene_name STRING,
    gene_sequence TEXT
);

CREATE TABLE sequences (
    sequence_id UUID PRIMARY KEY,
    gene_id UUID REFERENCES genes(gene_id),
    sequence_data TEXT
);

CREATE TABLE GeneSequences (
	id INT PRIMARY KEY,
	gene_name VARCHAR(50),
	sequence TEXT
);

CREATE TABLE GeneComparisons (
	id INT PRIMARY KEY,
	gene1_id INT,
	gene2_id INT,
	comparison_result TEXT
);

CREATE TABLE AnalysisResults (
	id INT PRIMARY KEY,
	analysis_name VARCHAR(50),
	result TEXT
);

CREATE TABLE GeneMetadata (
	id INT PRIMARY KEY,
	gene_id INT,
	description TEXT
);

CREATE TABLE genes (
    gene_id INT PRIMARY KEY,
    gene_name VARCHAR(255),
    gene_sequence TEXT
);

CREATE TABLE sequences (
    sequence_id INT PRIMARY KEY,
    gene_id INT,
    sequence_text TEXT,
    FOREIGN KEY (gene_id) REFERENCES genes(gene_id)
);

CREATE TABLE users (
    user_id INT PRIMARY KEY,
    username VARCHAR(255),
    email VARCHAR(255)
);
