tables.table_schema:
CREATE TABLE dna_sequences (
  sequence_id INT PRIMARY KEY,
  sequence_name VARCHAR(50) NOT NULL,
  sequence_data TEXT NOT NULL
);

CREATE TABLE dna_sequences (
    id UUID PRIMARY KEY,
    sequence TEXT NOT NULL,
    name TEXT,
    description TEXT
);

CREATE TABLE sequence_tags (
    id UUID PRIMARY KEY,
    sequence_id UUID REFERENCES dna_sequences(id),
    tag TEXT,
    CONSTRAINT fk_sequence_id FOREIGN KEY(sequence_id) REFERENCES dna_sequences(id)
);

CREATE TABLE dna_sequences (
	dna_id INT PRIMARY KEY,
	sequence VARCHAR(100) NOT NULL,
	organism VARCHAR(50) NOT NULL,
	length INT NOT NULL
);

CREATE TABLE genes (
	gene_id INT PRIMARY KEY,
	gene_name VARCHAR(50) NOT NULL,
	dna_id INT,
	FOREIGN KEY (dna_id) REFERENCES dna_sequences(dna_id)
);

CREATE TABLE proteins (
	protein_id INT PRIMARY KEY,
	protein_name VARCHAR(50) NOT NULL,
	dna_id INT,
	FOREIGN KEY (dna_id) REFERENCES dna_sequences(dna_id)
);

CREATE TABLE annotations (
	annotation_id INT PRIMARY KEY,
	annotation_text TEXT NOT NULL,
	protein_id INT,
	FOREIGN KEY (protein_id) REFERENCES proteins(protein_id)
);

CREATE TABLE dna_sequences (
  id INT PRIMARY KEY,
  sequence TEXT NOT NULL,
  description TEXT
);

CREATE TABLE sequence_tags (
  tag_id INT PRIMARY KEY,
  sequence_id INT,
  tag TEXT NOT NULL,
  FOREIGN KEY (sequence_id) REFERENCES dna_sequences(id)
);

CREATE TABLE sequence_annotations (
  annotation_id INT PRIMARY KEY,
  sequence_id INT,
  annotation TEXT NOT NULL,
  FOREIGN KEY (sequence_id) REFERENCES dna_sequences(id)
);

CREATE TABLE sequence_references (
  reference_id INT PRIMARY KEY,
  sequence_id INT,
  reference_link TEXT NOT NULL,
  FOREIGN KEY (sequence_id) REFERENCES dna_sequences(id)
);
