    user_id INT NOT NULL,
    role VARCHAR(50) NOT NULL
);

CREATE TABLE DNAData (
    id SERIAL PRIMARY KEY,
    dna_sequence TEXT NOT NULL,
    owner_id INT NOT NULL
);

CREATE TABLE AuthorizationLogs (
    id SERIAL PRIMARY KEY,
    user_id INT NOT NULL,
    action VARCHAR(50) NOT NULL,
    log_timestamp TIMESTAMP
);

CREATE TABLE AccessControls (
    id INT PRIMARY KEY,
    user_id INT,
    permission VARCHAR(50)
);

CREATE TABLE DNAData (
    id INT PRIMARY KEY,
    sample_id INT,
    dna_sequence TEXT,
    owner_id INT
);

CREATE TABLE Users (
    user_id INT PRIMARY KEY,
    username VARCHAR(50),
    password VARCHAR(50),
    role ENUM('admin','scientist','researcher')
);

CREATE TABLE Permissions (
    permission_id INT PRIMARY KEY,
    permission_name VARCHAR(50)
);

CREATE TABLE authorized_personnel (
    id INT PRIMARY KEY,
    name VARCHAR(255) NOT NULL,
    role VARCHAR(255) NOT NULL
);

CREATE TABLE access_logs (
    log_id SERIAL PRIMARY KEY,
    personnel_id INT NOT NULL,
    accessed_table VARCHAR(255) NOT NULL,
    access_date TIMESTAMP NOT NULL,
    FOREIGN KEY (personnel_id) REFERENCES authorized_personnel(id)
);

CREATE TABLE dna_data (
    dna_id SERIAL PRIMARY KEY,
    dna_sequence TEXT NOT NULL
);

CREATE TABLE permission_roles (
    role_id SERIAL PRIMARY KEY,
    role_name VARCHAR(255) NOT NULL
);

CREATE TABLE data_permissions (
    permission_id SERIAL PRIMARY KEY,
    dna_id INT NOT NULL,
    role_id INT NOT NULL,
    FOREIGN KEY (dna_id) REFERENCES dna_data(dna_id),
    FOREIGN KEY (role_id) REFERENCES permission_roles(role_id)
);
