tables.table_schema:
CREATE TABLE dna_sequences (
  id UUID PRIMARY KEY,
  sequence TEXT NOT NULL
);

CREATE TABLE sequencing_results (
  id UUID PRIMARY KEY,
  sequence_id UUID REFERENCES dna_sequences(id),
  result TEXT NOT NULL
);

CREATE TABLE dna_sequences (
  id UUID PRIMARY KEY,
  sequence TEXT NOT NULL
);

CREATE TABLE processing_jobs (
  job_id SERIAL PRIMARY KEY,
  start_time TIMESTAMP NOT NULL,
  end_time TIMESTAMP,
  status VARCHAR(50) NOT NULL
);

CREATE TABLE transaction_logs (
  log_id SERIAL PRIMARY KEY,
  transaction_time TIMESTAMP NOT NULL,
  transaction_type VARCHAR(50) NOT NULL
);

CREATE TABLE Sequence (
    sequence_id INT PRIMARY KEY,
    dna_sequence TEXT
);

CREATE TABLE ProcessingLog (
    log_id INT PRIMARY KEY,
    sequence_id INT REFERENCES Sequence(sequence_id),
    start_time TIMESTAMP,
    end_time TIMESTAMP
);

CREATE TABLE TransactionRecord (
    record_id INT PRIMARY KEY,
    log_id INT REFERENCES ProcessingLog(log_id),
    transaction_type TEXT,
    transaction_time TIMESTAMP
);

CREATE TABLE ErrorLog (
    error_id INT PRIMARY KEY,
    log_id INT REFERENCES ProcessingLog(log_id),
    error_message TEXT
);

CREATE TABLE Sequence (
  sequence_id INT PRIMARY KEY,
  dna_sequence TEXT
);

CREATE TABLE Transaction (
  transaction_id INT PRIMARY KEY,
  timestamp TIMESTAMP,
  user_id INT
);

CREATE TABLE User (
  user_id INT PRIMARY KEY,
  username TEXT,
  email TEXT,
  password TEXT
);
