tables.table_schema:
CREATE TABLE traffic_updates (
  route_id INT,
  timestamp TIMESTAMP,
  traffic_info VARCHAR(255),
  CONSTRAINT fk_route_id FOREIGN KEY (route_id) REFERENCES delivery_routes(route_id)
);

CREATE TABLE routes (
  route_id INT PRIMARY KEY,
  start_location VARCHAR(50),
  end_location VARCHAR(50),
  distance FLOAT
);

CREATE TABLE traffic_conditions (
  condition_id INT PRIMARY KEY,
  condition_description VARCHAR(100),
  road_closure BOOLEAN,
  traffic_level INT
);

CREATE TABLE vehicles (
  vehicle_id INT PRIMARY KEY,
  vehicle_name VARCHAR(50),
  vehicle_type VARCHAR(50),
  driver_name VARCHAR(50)
);

CREATE TABLE assignments (
  assignment_id INT PRIMARY KEY,
  route_id INT,
  vehicle_id INT,
  assignment_date DATE
);

CREATE TABLE efficiency_data (
  efficiency_id INT PRIMARY KEY,
  vehicle_id INT,
  route_id INT,
  traffic_time INT
);
