tables.table_schema:
CREATE TABLE vehicle_speed (
  vehicle_id INT PRIMARY KEY,
  speed FLOAT,
  timestamp TIMESTAMP
);

CREATE TABLE vehicle_speed_summary (
  vehicle_id INT PRIMARY KEY,
  date DATE,
  average_speed FLOAT,
  max_speed FLOAT
);

CREATE TABLE vehicle_speeds (
  id INT PRIMARY KEY,
  vehicle_id INT,
  timestamp TIMESTAMP,
  speed DECIMAL
);

CREATE TABLE vehicle_info (
  vehicle_id INT PRIMARY KEY,
  make VARCHAR(255),
  model VARCHAR(255),
  color VARCHAR(50)
);

CREATE TABLE Vehicle (id INT PRIMARY KEY,
make VARCHAR,
model VARCHAR);

CREATE TABLE SpeedData (id INT PRIMARY KEY,
gps_data_id INT,
speed DECIMAL);
