tables.table_schema:
CREATE TABLE vehicles (
    vehicle_id INT PRIMARY KEY,
    current_location VARCHAR(100),
    route_data JSON
);

CREATE TABLE estimated_arrival_times (
    vehicle_id INT,
    estimated_arrival_time TIMESTAMP,
    FOREIGN KEY (vehicle_id) REFERENCES vehicles(vehicle_id)
);

CREATE TABLE vehicle_locations (
  vehicle_id INT PRIMARY KEY,
  current_location TEXT NOT NULL,
  timestamp TIMESTAMP NOT NULL
);

CREATE TABLE routes (
  route_id INT PRIMARY KEY,
  route_name TEXT NOT NULL,
  distance FLOAT NOT NULL
);

CREATE TABLE estimated_arrival_time (
  vehicle_id INT PRIMARY KEY,
  route_id INT NOT NULL,
  estimated_time TIMESTAMP NOT NULL
);

CREATE TABLE vehicle_locations (
    vehicle_id INT PRIMARY KEY,
    current_location VARCHAR(255),
    route_data VARCHAR(255)
);

CREATE TABLE route_data (
    route_id INT PRIMARY KEY,
    route_name VARCHAR(255),
    estimated_time INT
);

CREATE TABLE arrival_estimations (
    estimation_id INT PRIMARY KEY,
    vehicle_id INT,
    route_id INT,
    estimated_arrival_time TIMESTAMP
);

CREATE TABLE vehicle_routes (
    vehicle_id INT,
    route_id INT
);
