tables.table_schema:
CREATE TABLE Geofencing (
    geofence_id INT PRIMARY KEY,
    location_name VARCHAR(255),
    radius_in_meters FLOAT,
    marketing_message TEXT
);

CREATE TABLE Geofence (
    id INT PRIMARY KEY,
    name VARCHAR(255),
    latitude DECIMAL,
    longitude DECIMAL
);

CREATE TABLE MarketingMessages (
    id INT PRIMARY KEY,
    message TEXT,
    geofence_id INT,
    timestamp TIMESTAMP
);

CREATE TABLE geofence_areas (
    id INT PRIMARY KEY,
    area_name VARCHAR(255),
    latitude FLOAT,
    longitude FLOAT
);

CREATE TABLE users (
    id INT PRIMARY KEY,
    username VARCHAR(255),
    email VARCHAR(255),
    phone_number VARCHAR(20)
);

CREATE TABLE geofences (
    geofence_id UUID PRIMARY KEY,
    geofence_name VARCHAR(255),
    latitude FLOAT,
    longitude FLOAT
);

CREATE TABLE marketing_messages (
    message_id UUID PRIMARY KEY,
    message_text TEXT,
    start_date TIMESTAMP,
    end_date TIMESTAMP
);

CREATE TABLE user_preferences (
    preference_id UUID PRIMARY KEY,
    user_id UUID,
    receive_messages BOOLEAN,
    receive_notifications BOOLEAN
);
