tables.table_schema:
CREATE TABLE advertising_placements (
    placement_id UUID PRIMARY KEY,
    location_id UUID,
    advertiser_id UUID,
    traffic_volume INT
);

CREATE TABLE high_traffic_areas (
    area_id INT PRIMARY KEY,
    area_name VARCHAR(50),
    area_population INT,
    traffic_volume INT
);

CREATE TABLE billboards (
    billboard_id INT PRIMARY KEY,
    location VARCHAR(100),
    dimensions VARCHAR(20),
    price DECIMAL(10,2)
);

CREATE TABLE high_traffic_areas (
    area_id INT PRIMARY KEY,
    area_name VARCHAR(255),
    traffic_level INT
);

CREATE TABLE advertisers (
    advertiser_id INT PRIMARY KEY,
    advertiser_name VARCHAR(255)
);

CREATE TABLE traffic_data (
    data_id INT PRIMARY KEY,
    area_id INT,
    date DATE,
    traffic_count INT
);

CREATE TABLE Advertisers (
   advertiser_id INT PRIMARY KEY,
   advertiser_name VARCHAR(255) NOT NULL,
   contact_name VARCHAR(255),
   contact_email VARCHAR(255) NOT NULL
);

CREATE TABLE AdvertisingSpaces (
   space_id INT PRIMARY KEY,
   space_location VARCHAR(255) NOT NULL,
   space_size INT,
   price DECIMAL(10,2) NOT NULL
);

CREATE TABLE AdImpressions (
   impression_id INT PRIMARY KEY,
   campaign_id INT NOT NULL,
   impression_date DATE,
   impression_count INT
);

CREATE TABLE AdClicks (
   click_id INT PRIMARY KEY,
   impression_id INT NOT NULL,
   click_date DATE,
   click_count INT
);
