tables.table_schema:
CREATE TABLE geofenced_locations (
  location_id INT PRIMARY KEY,
  location_name VARCHAR(50),
  latitude DECIMAL(10, 8),
  longitude DECIMAL(11, 8)
);

CREATE TABLE user_access_permissions (
  user_id INT PRIMARY KEY,
  location_id INT,
  permission_level VARCHAR(20),
  FOREIGN KEY (location_id) REFERENCES geofenced_locations(location_id)
);

CREATE TABLE Users (
    user_id BIGINT PRIMARY KEY,
    username TEXT NOT NULL,
    proximity_threshold FLOAT NOT NULL
);

CREATE TABLE Locations (
    location_id BIGINT PRIMARY KEY,
    location_name TEXT NOT NULL,
    location_lat FLOAT NOT NULL,
    location_lng FLOAT NOT NULL
);

CREATE TABLE Permissions (
    permission_id BIGINT PRIMARY KEY,
    user_id BIGINT NOT NULL,
    location_id BIGINT NOT NULL,
    permission_level TEXT NOT NULL
);

CREATE TABLE users (
    user_id INT PRIMARY KEY,
    username VARCHAR(50),
    location_id INT,
    permission_level VARCHAR(20)
);

CREATE TABLE locations (
    location_id INT PRIMARY KEY,
    location_name VARCHAR(50),
    latitude DECIMAL(10,8),
    longitude DECIMAL(11,8)
);

CREATE TABLE proximity_rules (
    rule_id INT PRIMARY KEY,
    location_id INT,
    distance_threshold DECIMAL(10,2),
    permission_level VARCHAR(20)
);

CREATE TABLE access_logs (
    log_id INT PRIMARY KEY,
    user_id INT,
    location_id INT,
    access_time TIMESTAMP
);

CREATE TABLE geofenced_locations (
    location_id UUID PRIMARY KEY,
    latitude DECIMAL NOT NULL,
    longitude DECIMAL NOT NULL,
    radius DECIMAL NOT NULL
);

CREATE TABLE users (
    user_id UUID PRIMARY KEY,
    username VARCHAR(50) NOT NULL,
    email VARCHAR(100) NOT NULL
);

CREATE TABLE access_permissions (
    permission_id UUID PRIMARY KEY,
    user_id UUID REFERENCES users(user_id),
    location_id UUID REFERENCES geofenced_locations(location_id),
    permission_level INT NOT NULL
);

CREATE TABLE user_location (
    user_id UUID REFERENCES users(user_id),
    last_known_location_id UUID REFERENCES geofenced_locations(location_id)
);

CREATE TABLE user_activity (
    activity_id UUID PRIMARY KEY,
    user_id UUID REFERENCES users(user_id),
    timestamp TIMESTAMP NOT NULL,
    activity_description TEXT
);
