tables.table_schema:
CREATE TABLE users (
    user_id UUID PRIMARY KEY,
    username VARCHAR(50) NOT NULL,
    email VARCHAR(100) NOT NULL,
    personalized_ar_data JSONB
);

CREATE TABLE ar_experiences (
    ar_id UUID PRIMARY KEY,
    user_id UUID REFERENCES users(user_id),
    ar_experience_name VARCHAR(100) NOT NULL,
    ar_content BYTEA
);

CREATE TABLE users (
    user_id UUID PRIMARY KEY,
    username VARCHAR(50) NOT NULL,
    email VARCHAR(100) UNIQUE,
    password VARCHAR(100) NOT NULL
);

CREATE TABLE user_preferences (
    preference_id UUID PRIMARY KEY,
    user_id UUID REFERENCES users(user_id),
    favorite_category VARCHAR(50),
    show_notifications BOOLEAN DEFAULT TRUE
);

CREATE TABLE users (
    user_id INT PRIMARY KEY,
    username VARCHAR(255) NOT NULL,
    email VARCHAR(255) UNIQUE,
    age INT
);

CREATE TABLE ar_experiences (
    experience_id INT PRIMARY KEY,
    user_id INT REFERENCES users(user_id),
    experience_name VARCHAR(255),
    date_created TIMESTAMP
);
