tables.table_schema:
CREATE TABLE User_Profile (
    user_id INT PRIMARY KEY,
    username VARCHAR(50) NOT NULL,
    email VARCHAR(50) NOT NULL,
    last_interaction_timestamp TIMESTAMP
);

CREATE TABLE ar_content (
    id INT PRIMARY KEY,
    content_name VARCHAR(255),
    content_description TEXT,
    creation_timestamp TIMESTAMP
);

CREATE TABLE Placement (
    placement_id INT PRIMARY KEY,
    ar_content_id INT,
    user_id INT,
    placement_timestamp TIMESTAMP
);

CREATE TABLE ARContent (
    ar_content_id INT PRIMARY KEY,
    ar_name VARCHAR(50),
    ar_description TEXT,
    ar_type VARCHAR(20)
);

CREATE TABLE InteractionLogs (
    log_id INT PRIMARY KEY,
    interaction_id INT,
    log_details JSONB
);

CREATE TABLE content_details (
    content_id INT PRIMARY KEY,
    content_name VARCHAR(100),
    content_type VARCHAR(50),
    content_size INT
);

CREATE TABLE user_preferences (
    user_id INT PRIMARY KEY,
    preference_1 VARCHAR(50),
    preference_2 VARCHAR(50),
    preference_3 VARCHAR(50)
);
